import React, { useState } from "react";
import axios from "axios";

function App() {
  const [videoUrl, setVideoUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [comments, setComments] = useState([]);
  const [summary, setSummary] = useState("");
  const [error, setError] = useState("");

  const extractVideoId = (url) => {
    try {
      const parsed = new URL(url);
      const hostname = parsed.hostname;

      if (hostname === "youtu.be") {
        return parsed.pathname.slice(1);
      } else if (hostname.includes("youtube.com")) {
        if (parsed.searchParams.has("v")) {
          return parsed.searchParams.get("v");
        } else if (parsed.pathname.startsWith("/embed/")) {
          return parsed.pathname.split("/embed/")[1];
        }
      }
      return null;
    } catch (err) {
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    const videoId = extractVideoId(videoUrl);
    if (!videoId) {
      setError("Invalid YouTube URL");
      return;
    }

    setLoading(true);
    setComments([]);
    setSummary("");

    try {
      const res = await axios.post("http://localhost:5000/api/comments", { videoId });
      setComments(res.data.comments);
      setSummary(res.data.summary);
    } catch (err) {
      setError("Failed to fetch comments or summary");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "auto", padding: 20, fontFamily: "Arial" }}>
      <h2>YouTube Comment Summarizer</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter YouTube video URL"
          value={videoUrl}
          onChange={(e) => setVideoUrl(e.target.value)}
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <button type="submit" disabled={loading} style={{ padding: "8px 16px" }}>
          {loading ? "Analyzing..." : "Analyze"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {summary && (
        <div style={{ marginTop: 20 }}>
          <h3>Summary & Insights</h3>
          <p>{summary}</p>
        </div>
      )}

      {comments.length > 0 && (
        <div style={{ marginTop: 20 }}>
          <h3>Sample Comments</h3>
          <ul style={{ maxHeight: 200, overflowY: "auto" }}>
            {comments.slice(0, 10).map((c, i) => (
              <li key={i} style={{ marginBottom: 8 }}>{c}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
