import React, { useState } from "react";
import axios from "axios";

function App() {
  const [mode, setMode] = useState("url");
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [comments, setComments] = useState([]);
  const [summary, setSummary] = useState("");
  const [keywords, setKeywords] = useState([]);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);
    setComments([]);
    setSummary("");
    setKeywords([]);

    const endpoint = mode === "url" ? "url" : "theme";
    const payload = mode === "url"
      ? { videoId: extractVideoId(input) }
      : { theme: input };

    if (mode === "url" && !payload.videoId) {
      setError("Invalid YouTube URL.");
      setLoading(false);
      return;
    }

    try {
      const res = await axios.post(`http://localhost:5000/api/comments/${endpoint}`, payload);
      setComments(res.data.comments);
      setSummary(res.data.summary);
      setKeywords(res.data.keywords);
    } catch (err) {
      setError("Something went wrong.");
    } finally {
      setLoading(false);
    }
  };

  const extractVideoId = (url) => {
    try {
      const parsed = new URL(url);
      if (parsed.hostname === "youtu.be") return parsed.pathname.slice(1);
      if (parsed.hostname.includes("youtube.com")) {
        if (parsed.searchParams.has("v")) return parsed.searchParams.get("v");
        if (parsed.pathname.startsWith("/embed/")) return parsed.pathname.split("/embed/")[1];
      }
    } catch {}
    return null;
  };

  return (
    <div style={{ maxWidth: 700, margin: "auto", padding: 20, fontFamily: "Arial" }}>
      <h2>YouTube Comment Analyzer</h2>

      <div style={{ marginBottom: 10 }}>
        <label>
          <input
            type="radio"
            value="url"
            checked={mode === "url"}
            onChange={() => setMode("url")}
          /> Analyze by Video URL
        </label>
        <label style={{ marginLeft: 20 }}>
          <input
            type="radio"
            value="theme"
            checked={mode === "theme"}
            onChange={() => setMode("theme")}
          /> Analyze by Theme
        </label>
      </div>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder={mode === "url" ? "Enter YouTube URL" : "Enter theme or topic"}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <button type="submit" disabled={loading} style={{ padding: "8px 16px" }}>
          {loading ? "Processing..." : "Analyze"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {summary && (
  <div style={{ marginTop: 20 }}>
    <h3>📊 AI Summary: Emotional and Public Opinion Insights</h3>
    {summary.split("\n").map((line, idx) => (
      <p key={idx} style={{ margin: "6px 0", lineHeight: "1.4em" }}>{line.trim()}</p>
    ))}
  </div>
)}


{keywords.length > 0 && (
  <div style={{ marginTop: 20 }}>
    <h3>🔑 Extracted Keywords</h3>
    <div style={{ display: "flex", flexWrap: "wrap", gap: "8px", marginTop: 10 }}>
      {keywords.map((kw, i) => (
        <span key={i} style={{
          background: "#eef",
          padding: "6px 12px",
          borderRadius: "20px",
          fontSize: "0.85rem"
        }}>{kw}</span>
      ))}
    </div>
  </div>
)}


{comments.length > 0 && (
  <div style={{ marginTop: 30 }}>
    <h3>💬 Top Comments (showing {comments.length})</h3>
    <ul style={{ maxHeight: 300, overflowY: "auto", paddingLeft: 0 }}>
      {comments.map((c, i) => (
        <li key={i} style={{
          listStyle: "none",
          marginBottom: 14,
          paddingBottom: 8,
          borderBottom: "1px solid #ddd"
        }}>
          <p style={{ marginBottom: 4 }}>
            <strong>@{c.username}</strong> &nbsp; 
            <span style={{ color: "#555" }}>({c.likes} likes)</span> — 
            <em style={{ color: c.sentiment === 'positive' ? 'green' : c.sentiment === 'negative' ? 'red' : '#555' }}>
              &nbsp;{c.sentiment}
            </em>
          </p>
          <p style={{ margin: 0 }}>{c.text}</p>
        </li>
      ))}
    </ul>
  </div>
)}

    </div>
  );
}

export default App;
